package testing;

public class Nestedfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// for
		
// for - iterate until the condition false
		
	for(int i=0;i<=2;i++)
	{
		for(int j=0;j<=2;j++)
		{
			System.out.println(i+ " " + j);
		}
	}
				
	}

}
